// You are in a city that consists of n intersections numbered from 0 to n - 1 with bi-directional roads between some intersections. The inputs are generated such that you can reach any intersection from any other intersection and that there is at most one road between any two intersections.

// You are given an integer n and a 2D integer array roads where roads{i} = {ui, vi, timei} means that there is a road between intersections ui and vi that takes timei minutes to travel. You want to know in how many ways you can travel from intersection 0 to intersection n - 1 in the shortest amount of time.

// Return the number of ways you can arrive at your destination in the shortest amount of time. Since the answer may be large, return it modulo 109 + 7.


//[[0,6,7],[0,1,2],[1,2,3],[1,3,3],[6,3,3],[3,5,1],[6,5,1],[2,5,1],[0,4,5],[4,6,2]]

#include<iostream>
#include<vector>
#include<queue>
using namespace std;


class Solution {
   vector<int>way;
   vector<vector<pair<int,int>>>adj;
public:
    int countPaths(int n, vector<vector<int>>& grid) {
        adj.resize(n);
        way.resize(n,0);
        priority_queue<pair<long long,long long>,vector<pair<long long,long long>>,greater<pair<long long,long long>>>pq;
        for(auto temp_pair:grid){
            adj[temp_pair[0]].push_back({temp_pair[1],temp_pair[2]});
            adj[temp_pair[1]].push_back({temp_pair[0],temp_pair[2]});
        }
        int mod = (int)(1e9 + 7);
        vector<long long>time_vector(n,INT_MAX);
        way[0]=1;
        pq.push({0,0});
        time_vector[0]=0;
        while(!pq.empty()){
            auto temp=pq.top();
            long long origin=temp.second;
            long long origin_time=temp.first;
            long long origin_way=way[origin];
            pq.pop();
            if(origin_time>time_vector[origin])continue;
            for(auto adj_pair:adj[origin]){
               long long adj_node=adj_pair.first;
               long long adj_time=adj_pair.second;
               if(adj_time+origin_time==time_vector[adj_node]){
                  way[adj_node]=(origin_way+way[adj_node])%mod;
               }
               if(origin_time+adj_time<time_vector[adj_node]){
                   time_vector[adj_node]=origin_time+adj_time;
                   way[adj_node]=origin_way;
                   pq.push({time_vector[adj_node],adj_node});
               }
            }
        }
        return way[n-1]%mod;
    }
};
